import 'package:know_your_medic/modules/symptoms_module.dart';

class MedicApi {

  Future<void> callSymptomsData() async {
    try {
      
      var url = Uri.parse('https://healthservice.priaid.ch/symptoms?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.._3YXktwfcyWwFpTzHPF92OypF5_QdDvfbQoY4SoIF4M&format=json&language=en-gb');
      Response response = await get(url);
      List data = jsonDecode(response.body);
      Symptoms.symptoms = data;

    } catch(e) {
      print(e.toString());
    }
  }

}