import 'dart:convert';
import 'package:flutter/cupertino.dart';
import 'package:http/http.dart';
import 'package:know_your_medic/modules/symptoms_module.dart';

class MedicApi {

  Future<void> callSymptomsData() async {
    try {
      
      var url = Uri.parse('https://sandbox-healthservice.priaid.ch/symptoms?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..llzQdvElvr1zp8nSJkN-PbscpCGLnz-H_aCQvcQDGwE&format=json&language=en-gb');
      Response response = await get(url);
      var data = jsonDecode(response.body);
      for(int i = 0; i < data.length; i++) {
        Symptoms.symptoms.add(data[i]);
      }
      print('added');

    } catch(e) {
      print(e.toString());
    }
  }

}